mapper.update:
update pharmacy.base_provider
set isDel = 1
where id = #{id}

mapper.select:
select base_provider.id, base_provider.name, base_provider.address, base_provider.type, base_provider_type.type, base_provider.createBy, base_provider.createDate, base_provider.updateBy, base_provider.updateDate, base_provider.isDel, base_provider_type.id, base_provider_type.type from pharmacy.base_provider, pharmacy.base_provider_type where base_provider.type = base_provider_type.id and base_provider.isDel = 0
and base_provider.name = #{name}
and base_provider.type = #{type}
