mapper.select:
SELECT a.id, a.code, a.categoryId, (SELECT accountsCategoryName FROM cw_category WHERE a.categoryId = id) accountsCategoryName, a.orderCode, a.cost, a.bankAcountId, (SELECT bandCount FROM bank_account WHERE a.bankAcountId=id) bandCount, a.description, a.createTime, a.modificationTime, a.createBy, (SELECT userName FROM sys_user WHERE a.createBy = userId) createName, a.modificationBy, (SELECT userName FROM sys_user WHERE a.modificationBy = userId) modificationName FROM cw_accounts a where isDel=0
and a.code like concat('%',#{code},'%')
and a.categoryId=#{categoryId}
and a.orderCode like concat('%',#{orderCode},'%')
and a.createTime BETWEEN #{beginTime} and #{endTime}

SELECT YEAR(a.createTime) AS year, MONTH(a.createTime) AS month, COUNT(CASE WHEN b.parentId = 2 THEN a.id ELSE NULL END) AS inCount, SUM(CASE WHEN b.parentId = 2 THEN a.cost ELSE 0 END) AS totalInAmount, COUNT(CASE WHEN b.parentId = 1 THEN a.id ELSE NULL END) AS outCount, SUM(CASE WHEN b.parentId = 1 THEN a.cost ELSE 0 END) AS totalOutAmount FROM cw_accounts a JOIN cw_category b ON a.categoryId = b.id WHERE a.isDel = 0
AND YEAR(a.createTime) = #{year}
GROUP BY YEAR(a.createTime), MONTH(a.createTime) ORDER BY YEAR(a.createTime), MONTH(a.createTime);

SELECT COUNT(CASE WHEN b.parentId = 1 THEN a.id ELSE NULL END) AS outCount, SUM(CASE WHEN b.parentId = 1 THEN a.cost ELSE 0 END) AS totalOutAmount, COUNT(CASE WHEN b.parentId = 2 THEN a.id ELSE NULL END) AS inCount, SUM(CASE WHEN b.parentId = 2 THEN a.cost ELSE 0 END) AS totalInAmount FROM cw_accounts a JOIN cw_category b ON a.categoryId = b.id WHERE a.isDel = 0
AND YEAR(a.createTime) = #{year}
AND MONTH(a.createTime) = #{month}
