mapper.update:
update pharmacy.base_provider
set isDel = 1
where id = #{id}

mapper.select:
SELECT base_provider.id, base_provider.name, base_provider.address, base_provider.type, base_provider_type.type AS typeName, base_provider.createBy, create_user.userName AS createMan, base_provider.createDate, base_provider.updateBy, update_user.userName AS updateMan, base_provider.updateDate, base_provider.isDel, base_provider_type.id AS providerTypeId FROM pharmacy.base_provider INNER JOIN pharmacy.base_provider_type ON base_provider.type = base_provider_type.id LEFT JOIN pharmacy.sys_user AS create_user ON base_provider.createBy = create_user.userId LEFT JOIN pharmacy.sys_user AS update_user ON base_provider.updateBy = update_user.userId WHERE base_provider.isDel = 0
AND base_provider.name = #{name}
AND base_provider.type = #{type}
