mapper.select:
SELECT o.*,t.name AS 'cgtype',(SELECT userName FROM sys_user u WHERE o.demanderBy=u.userId)AS 'demanderUserName', (SELECT userName FROM sys_user u WHERE o.approverBy=u.userId)AS 'approverUserName', (SELECT userName FROM sys_user u WHERE o.documenterBy=u.userId)AS 'documenterUserName', (SELECT userName FROM sys_user u WHERE o.updateBy=u.userId)AS 'updateUserName', s.type AS 'orderStatueName' FROM cgsq_order o,cg_type t,order_statue s WHERE o.type=t.id AND s.id=o.orderStatus
and o.code like concat('%',#{code},'%')
and o.subject like concat('%',#{subject},'%')
and o.type=#{type}
and o.createTime BETWEEN #{startTime} AND #{endTime}
AND o.isDel=0
AND o.voidState=#{voidState}
AND o.approvalStatus=#{approvalStatus}
order by o.createTime Desc

SELECT o.*,t.name AS 'cgtype',(SELECT userName FROM sys_user u WHERE o.demanderBy=u.userId)AS 'demanderUserName', (SELECT userName FROM sys_user u WHERE o.approverBy=u.userId)AS 'approverUserName', (SELECT userName FROM sys_user u WHERE o.documenterBy=u.userId)AS 'documenterUserName', (SELECT userName FROM sys_user u WHERE o.updateBy=u.userId)AS 'updateUserName', s.type AS 'orderStatueName' FROM cgsq_order o,cg_type t,order_statue s WHERE o.type=t.id AND s.id=o.orderStatus and o.orderStatus = 3
and o.code like concat('%',#{code},'%')
and o.subject like concat('%',#{subject},'%')
and o.type=#{type}
and o.createTime BETWEEN #{startTime} AND #{endTime}
AND o.isDel=0 order by o.createTime Desc
