mapper.select:
select id, code, subject, type, (select 'name' from cg_type ct where ct.id = a.type ) as cgType, (SELECT `name` FROM cg_type b WHERE a.type = b.id) AS orderTypeName,demanderBy, (SELECT `userName` FROM sys_user c WHERE a.demanderBy = c.userId) AS OrderTypeName,demandTime, count, referenceAmount, orderStatus, approvalStatus, remark, effectiveTime, approverBy, (SELECT `userName` FROM sys_user c WHERE a.approverBy = c.userId),approverRemark, documenterBy, (SELECT `userName` FROM sys_user c WHERE a.documenterBy = c.userId) AS documenterName, voidState, createTime, updateBy,(SELECT `userName` FROM sys_user c WHERE a.updateBy = c.userId) AS updateName, updateTime, providerId,(SELECT `name` FROM base_provider b WHERE b.id = a.providerId) AS providerName, contactperson, phone, fax, email, deliveryDate, deliveryBy, (SELECT `userName` FROM sys_user c WHERE a.deliveryBy = c.userId) AS deliveryName, payType, payTime, isPay from cgdd_order AS a
a.isDel = 0
and code like concat("%", #{code},"%")
and demandTime BETWEEN #{beginTime} and #{endTime}
and orderStatus=#{orderStatus}
and providerId=#{providerId}
and type=#{type}
order by a.createTime desc

SELECT CASE WHEN isPay = '0' THEN '未支付' ELSE '已支付' END AS paymentStatus, COUNT(*) AS orderCount, IFNULL(SUM(CASE WHEN isPay = '1' THEN referenceAmount ELSE 0 END), 0) AS totalAmount FROM cgdd_order WHERE isDel = 0
AND YEAR(createTime) = #{year}
AND MONTH(createTime) = #{month}
GROUP BY CASE WHEN isPay = '0' THEN '未支付' ELSE '已支付' END;

SELECT YEAR(createTime) AS year, MONTH(createTime) AS month, COUNT(*) AS allPayCount, SUM(CASE WHEN isPay = 1 THEN 1 ELSE 0 END) AS paidCount, SUM(CASE WHEN isPay = 0 THEN 1 ELSE 0 END) AS unpaidCount, SUM(CASE WHEN isPay = 1 THEN referenceAmount ELSE 0 END) AS totalPaidAmount, SUM(CASE WHEN isPay = 0 THEN referenceAmount ELSE 0 END) AS totalUnpaidAmount FROM cgdd_order WHERE isDel = 0
AND YEAR(createTime) = #{year}
GROUP BY year, month ORDER BY month
