mapper.select:
select userId, userName, password, age, sex, createDate, createBy, updateDate, updateBy, isDel, isState, nickName, email    from sys_user
where
    userName = #{username}
  and password = #{password}
  and isDel = 0

select userId, userName, password, age, sex, createDate, createBy, updateDate, updateBy, isDel, isState, nickName, email    from sys_user

    isDel = 0

        and userName like concat("%",#{username},"%")


        and sex = #{sex}


        and isState = #{isstate}
