mapper.select:
SELECT o.*,(SELECT userName FROM sys_user u WHERE o.createBy=u.userId)AS 'demanderUserName', (SELECT userName FROM sys_user u WHERE o.approverBy=u.userId)AS 'approverUserName', (SELECT userName FROM sys_user u WHERE o.documenterBy=u.userId)AS 'documenterUserName', (SELECT userName FROM sys_user u WHERE o.updateBy=u.userId)AS 'updateUserName', s.type AS 'orderStatueName', (SELECT `name` FROM base_provider p WHERE p.id=o.providerId) AS 'providerName', co.`code` AS `sourceCode`,
co.count,co.referenceAmount FROM cgrk_order o,order_statue s,cgdd_order co WHERE s.id=o.orderStatus AND co.id=o.purchaseId
and o.code like concat('%',#{code},'%')
and o.subject like concat('%',#{subject},'%')
and o.providerId=#{providerId}
and o.createTime BETWEEN #{startTime} AND #{endTime}
AND o.isDel=0
AND o.voidState=#{voidState}
AND o.approvalStatus=#{approvalStatus}
order by o.createTime Desc
