mapper.select:
select id, code, warningType, warningCount, medicineId,(select name from base_medicine where medicineId = base_medicine.id) as medicineName,
       isKnow, createDate, createBy,(select userName from sys_user where kc_alarm.createBy = userId) as createName,
       handleDate, handleBy,(select userName from sys_user where kc_alarm.handleBy = userId) as updateName ,isDel, isRemind
from kc_alarm

    and isDel = 0

       and code like concat("%",#{code},"%")


        and createDate BETWEEN #{startTime} AND #{endTime}


        and warningType = #{warningType}


        and medicineId = #{medicineId}

    order by warningType desc
