mapper.select:
SELECT k.id, k.code, k.dispatchId, k.isDel,d.id as TempId, d.dispatchCode, d.aimStoreHouseId, (select name from base_storehouse where id=d.aimStoreHouseId) aimStoreHouseName, d.medicineId,(SELECT name FROM base_medicine WHERE id=d.medicineId) medicineName, d.quantity, d.price, d.batchCode, d.isDel, kd.id, kd.code, kd.subject, kd.dispatchTime, kd.price, kd.beforeWarehouseId,(select name from base_storehouse where id=kd.beforeWarehouseId) beforeWarehouseName, kd.remark, kd.orderStatus, kd.approverBy, kd.approvalStatus , kd.documenterBy, kd.voidState, kd.createDate, kd.updateDate, kd.updateBy,kd.isDel FROM kc_disfromware k,kc_dispatchdetails d,kc_dispatch kd WHERE kd.`code`=d.dispatchCode AND kd.id=k.dispatchId
and kd.code like concat('%',#{code},'%')
and kd.subject like concat('%',#{subject},'%')
and d.aimStoreHouseId=#{fowardWarhouseId}
and kd.beforeWarehouseId=#{beforeWarhouseId}
and kd.dispatchTime BETWEEN #{startTime} AND #{endTime}
AND k.isDel=0 order by kd.dispatchTime Desc
