mapper.select:
SELECT k.*,s.name as warehouseName,m.name as materialCategoryName,o.type as orderStatusName, (SELECT userName FROM sys_user u WHERE k.checkerBy=u.userId) AS checkerByName, (SELECT userName FROM sys_user u WHERE k.approverBy=u.userId) AS approverByName, (SELECT userName FROM sys_user u WHERE k.createBy=u.userId) AS createByName, (SELECT userName FROM sys_user u WHERE k.updateBy=u.userId) AS updateByName FROM kc_inventory k left JOIN base_storehouse s ON k.warehouseId=s.id left JOIN sys_user u ON k.createBy=u.userId left JOIN base_medicine m ON k.materialCategoryId=m.id left JOIN order_statue o ON k.orderStatus=o.id
and k.code like concat('%',#{code},'%')
and k.subject like concat('%',#{subject},'%')
and k.warehouseId=#{warehouseId}
and k.orderStatus=#{orderStatus}
and k.createTime BETWEEN #{startTime} AND #{endTime}
AND k.isdel=0
AND k.isVoided=#{isVoided}
AND k.isApproved=#{isApproved}
order by k.billDate Desc

SELECT * , (select name from base_storehouse s WHERE s.id=m.storehouseId) as warehouseName, (select name from base_medicine c WHERE c.id=m.medicineId ) as medecineName, (select name from base_provider b WHERE b.id=m.providerId ) as providername , (SELECT c.specification from base_medicine c WHERE m.medicineId =c.id) as unitName from kc_medicine m
and m.storehouseId=#{storehouseId}
and m.medicineId=#{materialCategoryId}
