mapper.select:
SELECT a.id, a.`code`, a.storehouseId, f.name AS storehouseName, a.reportedTypeId, h.reportedName AS reportedTypeName, a.approverBy, a.approvalStatus, a.approverRemark, a.documenterBy, a.createTime, a.createBy, a.modificationTime, a.modificationBy, b.userName AS approverName, c.userName AS documenterName, d.userName AS createName, e.userName AS modificationName FROM kc_reported a LEFT JOIN sys_user b ON a.approverBy = b.userId LEFT JOIN sys_user c ON a.documenterBy = c.userId LEFT JOIN sys_user d ON a.createBy = d.userId LEFT JOIN sys_user e ON a.modificationBy = e.userId LEFT JOIN base_storehouse f ON a.storehouseId = f.id LEFT JOIN kc_reportedtype h ON a.reportedTypeId = h.id WHERE a.isDel = 0
and a.storehouseId=#{storehouseId}
and a.reportedTypeId=#{reportedTypeId}
and a.createTime BETWEEN #{beginTime} and #{endTime}
and a.code like concat('%',#{code},'%')
